package com.example.digitalrefrige.model.dataHolder;


import androidx.databinding.BaseObservable;
import androidx.databinding.Bindable;
import androidx.room.Entity;
import androidx.room.PrimaryKey;

import com.example.digitalrefrige.BR;

import java.util.Date;
import java.util.Objects;

/**
 * Entity class for local database, id is PK and autogenerated by Room
 */
@Entity(tableName = "item_table")
public class Item extends BaseObservable {
    private int quantity;
    private String name;
    private String description;
    private Date expireDate;
    private String imgUrl;


    @PrimaryKey(autoGenerate = true)
    private long itemId;

    public Item(String name, String description, Date expireDate, String imgUrl) {
        this.name = name;
        this.description = description;
        this.expireDate = expireDate;
        this.imgUrl = imgUrl;
    }

    public Item(){
    }

    @Bindable
    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
        notifyPropertyChanged(BR.name);
    }

    @Bindable
    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
        notifyPropertyChanged(BR.description);
    }

    public long getItemId() {
        return itemId;
    }

    public void setItemId(long itemId) {
        this.itemId = itemId;
    }

    @Bindable
    public int getQuantity() {
        return quantity;
    }

    public void setQuantity(int quantity) {
        this.quantity = quantity;
        notifyPropertyChanged(BR.quantity);
    }

    public void setExpireDate(Date expireDate) {
        this.expireDate = expireDate;
    }

    public Date getExpireDate() {
        return this.expireDate;
    }

    public void setImgUrl(String imgUrl) {
        this.imgUrl = imgUrl;
    }

    public String getImgUrl() {
        return this.imgUrl;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Item item = (Item) o;
        return itemId == item.itemId;
    }

    @Override
    public int hashCode() {
        return Objects.hash(itemId);
    }
}
