package com.example.digitalrefrige.model.dataHolder;

import androidx.databinding.BaseObservable;
import androidx.databinding.Bindable;
import androidx.room.Entity;
import androidx.room.PrimaryKey;

import com.example.digitalrefrige.BR;

import java.util.Objects;

/**
 * Entity class for local database, id is PK and autogenerated by Room
 */
@Entity(tableName = "label_table")
public class Label extends BaseObservable {
    public static Label NONE_LABEL = new Label("UNDEFINED");

    @PrimaryKey(autoGenerate = true)
    private long labelId;

    private String title;

    public Label(String title) {
        this.title = title;
    }
    public Label(){ }

    public void setTitle(String title) {
        this.title = title;
        notifyPropertyChanged(BR.title);
    }

    @Bindable
    public String getTitle() {
        return this.title;
    }

    public long getLabelId() {
        return labelId;
    }

    public void setLabelId(long labelId) {
        this.labelId = labelId;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Label label = (Label) o;
        return labelId == label.labelId;
    }

    @Override
    public int hashCode() {
        return Objects.hash(labelId);
    }

    @Override
    public String toString() {
        return "Label{" +
                "labelId=" + labelId +
                ", title='" + title + '\'' +
                '}';
    }

}
